mapper.select:
select course_timetable.id, room_name, room_number, campus_name, continuing_session, course_id, attend_class_teacher, student_count, class_day, class_order, start_week, end_week, class_in_school_week, course_sequence_number, week_description, class_distinct, course_timetable.term_year, course_timetable.term_order, gmt_create from student_course_time_table
join course_timetable on student_course_time_table.course_timetable_id = course_timetable.id
and student_id = #{studentId}
and student_course_time_table.term_year = #{termYear}
and student_course_time_table.term_order = #{termOrder}

select course_timetable.id, room_name, room_number, campus_name, continuing_session, course_id, attend_class_teacher, student_count, class_day, class_order, start_week, end_week, class_in_school_week, course_sequence_number, week_description, class_distinct, course_timetable.term_year, course_timetable.term_order, gmt_create from class_course_timetable
join course_timetable on class_course_timetable.course_timetable_id = course_timetable.id
and class_id = #{classId}
and class_course_timetable.term_year = #{termYear}
and class_course_timetable.term_order = #{termOrder}

select
from (
SELECT FROM course_timetable WHERE course_id = #{item.courseId,jdbcType=VARCHAR} and class_day = #{item.classDay,jdbcType=INTEGER} and class_order = #{item.classOrder,jdbcType=INTEGER} and start_week = #{item.startWeek,jdbcType=INTEGER} and end_week = #{item.endWeek,jdbcType=INTEGER} and course_sequence_number = #{item.courseSequenceNumber,jdbcType=VARCHAR} and term_year = #{item.termYear,jdbcType=CHAR} and term_order = #{item.termOrder,jdbcType=INTEGER}
) as ct
